import { useEffect, useState } from "react"

function Ad(){
    let [showTitle,setShowTitle] = useState(true)

    useEffect(()=>{
        let title = setTimeout(()=>{
            setShowTitle(!showTitle)
            return(
                clearTimeout(title)
            )
        },400)
        
    })
    return(
        <div className="adComp">
            <img className="adImg" src="/ad/img/내 손을 java.png" />
            <div className="adText">
                무대를 완전히 뒤집어 놓으셨다~!! 다시 찾는 아이유의 명곡중의 명곡~!!<br />
                프로그래밍 언어, 무엇을 배울까 고민한다면 아이유가 추천하는 내 손을 Java~!!!
            </div>
            {showTitle?
                <div className="adTitle">
                    내 손을 Java!!
                </div>:null
            }
        </div>
    )
}
export default Ad
