import { useState } from 'react';
import { Container, Nav, Navbar } from 'react-bootstrap';
import { Route, Routes } from 'react-router-dom';
import './App.css';
import database,{menuButton} from './data/db';

// 이펙트 : 로딩스피너, 스크롤바, 페이지 전환, 메뉴바 상단고정
function App() {
  const DB = database()  // DB 사용법 : DB.key
  const MENU_BTN = menuButton()
  let menu = [MENU_BTN.ca0Btn,MENU_BTN.ca1Btn,MENU_BTN.ca2Btn,MENU_BTN.ca3Btn,MENU_BTN.ca4Btn,MENU_BTN.ca5Btn,MENU_BTN.ca6Btn]
  let [menu2, setMenu2] = useState([])
  let [contentPath, setContentPath] = useState('/')

  return (
    <div className='container'>
      {/* 헤더 */}
      <div className='header'>header</div>

      {/* 메뉴바 */}
      <div className='menuBar'>
        <Navbar className='noMaPa'>
          <Container>
            <Nav>
              {selectMenu()}
            </Nav>
          </Container>
        </Navbar>
      </div>
      {/* 하위메뉴바 */}
      <div className='menuBar2'>
        <Navbar className='noMaPa'>
            <Container>
              <Nav>
                {menu2.map((data,i)=>{return(
                  <div key={data.id} >
                    <Nav.Link className='noMaPa'>{data}</Nav.Link>
                  </div>
                )})}
              </Nav>
            </Container>
          </Navbar>
      </div>

      {/* 스크롤바 */}
      <div className='scroll'>scroll</div>



      {/* 컨텐츠 */}
      <div className='contents' style={{display:'inline-block'}}>
        <Routes>
          <Route path="/" element={'contents'} />
        </Routes>
      </div>

      {/* 푸터 */}
      <div className='footer'>footer(제작자..블라블라)</div>
    </div>
  );

  function selectMenu(){
    for(var i in menu){
      return(
        <Nav.Link className='noMaPa' onClick={()=>{
          contentPath = menu[i].id
          setMenu2(selectMenu2('introduceIronMan'))
        }}>{MENU_BTN.ca0Btn.btn}</Nav.Link>
      )
    }
  }

  function selectMenu2(category){
    var tempMenuList = []
    for(var key in DB){
      if(DB[key].category == category){
        if(DB[key].series == 'Common'){
          tempMenuList.push(MENU_BTN.se0Btn.btn)
        }else if(DB[key].series == 'IronMan1'){
          tempMenuList.push(MENU_BTN.se1Btn.btn)
        }else if(DB[key].series == 'IronMan2'){
          tempMenuList.push(MENU_BTN.se2Btn.btn)
        }else if(DB[key].series == 'IronMan3'){
          tempMenuList.push(MENU_BTN.se3Btn.btn)
        }else if(DB[key].series == 'crossOver'){
          tempMenuList.push(MENU_BTN.se4Btn.btn)
        }
      }

    }
    return tempMenuList
  }



}// App 종료
export default App;
