import { useState } from 'react';
import { Container, Nav, Navbar } from 'react-bootstrap';
import { Route, Routes } from 'react-router-dom';
import './App.css';
import database from './data/db';

// 이펙트 : 로딩스피너, 스크롤바, 페이지 전환, 메뉴바 상단고정
function App() {
  const DB = database()  // DB 사용법 : DB.key

  return (
    <div className='container'>
      {/* 헤더 */}
      <div className='header'>header</div>

      {/* 메뉴바 */}
      <div className='menuBar'>
        <Navbar style={{margin:'0px', padding:'0px'}}>
          <Container>
            <Nav>
              <Nav.Link href="/">{DB.ca0Btn}</Nav.Link>
              <Nav.Link href="/">{DB.ca1Btn}</Nav.Link>
              <Nav.Link href="/">{DB.ca2Btn}</Nav.Link>
              <Nav.Link href="/">{DB.ca3Btn}</Nav.Link>
              <Nav.Link href="/">{DB.ca4Btn}</Nav.Link>
              <Nav.Link href="/">{DB.ca5Btn}</Nav.Link>
              <Nav.Link href="/">{DB.ca6Btn}</Nav.Link>
            </Nav>
          </Container>
        </Navbar>
      </div>
      {/* 하위메뉴바 */}
      <div className='menuBar2'>
        <Navbar>
            <Container>
              <Nav>
                <Nav.Link href="/">{DB.se0Btn}</Nav.Link>
                <Nav.Link href="/">{DB.se1Btn}</Nav.Link>
                <Nav.Link href="/">{DB.se2Btn}</Nav.Link>
                <Nav.Link href="/">{DB.se3Btn}</Nav.Link>
                <Nav.Link href="/">{DB.se4Btn}</Nav.Link>
              </Nav>
            </Container>
          </Navbar>
      </div>

      {/* 스크롤바 */}
      <div className='scroll'>scroll</div>



      {/* 컨텐츠 */}
      <div className='contents' style={{display:'inline-block'}}>
        <Routes>
          <Route path="/" element={'contents'} />
        </Routes>
      </div>

      {/* 푸터 */}
      <div className='footer'>footer(제작자..블라블라)</div>
    </div>
  );
}

export default App;
