import { useEffect, useState } from "react"

function Ad(){
    let [ads,setAds] = useState([
        {img:'내 손을 java.png',text1:'무대를 완전히 뒤집어 놓으셨다~!! 다시 찾는 아이유의 명곡중의 명곡~!!', text2:'프로그래밍 언어, 무엇을 배울까 고민한다면 아이유가 추천하는 내 손을 Java~!!!',title:'내 손을 Java!!'},
        {img:'',text1:'', text2:'',title:''},
    ])
    let [showTitle,setShowTitle] = useState(true)

    useEffect(()=>{
        let title = setTimeout(()=>{
            setShowTitle(!showTitle)
            return(
                clearTimeout(title)
            )
        },400)
        
    })
    return(
        <div className="adComp">
            <img className="adImg" src={"/ad/img/" + ads[0].img} />
            <div className="adText">{ads[0].text1}<br />{ads[0].text2}</div>
            {showTitle? <div className="adTitle">{ads[0].title}</div> : null}
        </div>
    )
}
export default Ad
