import { useState } from 'react';
import { Container, Nav, Navbar } from 'react-bootstrap';
import { Route, Routes } from 'react-router-dom';
import './App.css';
import database from './data/db';

// 이펙트 : 로딩스피너, 스크롤바, 페이지 전환
function App() {
  const DB = database()  // DB 사용법 : DB.key


  return (
    <div className='container'>
      {/* 헤더 */}
      <div className='header'>header</div>

      {/* 메뉴바 */}
      <div className='menuBar'>
        <Navbar sticky='top' bg="dark" variant="dark">
          <Container>
            <Nav>
              <Nav.Link href="/">Home</Nav.Link>
              <Nav.Link href="#features">Features</Nav.Link>
              <Nav.Link href="#pricing">Pricing</Nav.Link>
            </Nav>
          </Container>
        </Navbar>
      </div>

      {/* 스크롤바 */}
      <div className='scroll'>scroll</div>

      {/* 사이드메뉴바 */}
      <div className='sideMenu' style={{display:'inline-block'}}>sideMenu</div>

      {/* 컨텐츠 */}
      <div className='contents' style={{display:'inline-block'}}>
        <Routes>
          <Route path="/" element={'contents'} />
        </Routes>
      </div>

      {/* 푸터 */}
      <div className='footer'>footer(제작자..블라블라)</div>
    </div>
  );
}

export default App;
