              <Nav.Link className='noMaPa' onClick={()=>{

              }}>{MENU_BTN.ca4Btn}</Nav.Link>
              <Nav.Link className='noMaPa' onClick={()=>{

              }}>{MENU_BTN.ca5Btn}</Nav.Link>
              <Nav.Link className='noMaPa' onClick={()=>{

              }}>{MENU_BTN.ca6Btn}</Nav.Link>
            </Nav>
          </Container>
        </Navbar>
      </div>
      {/* 하위메뉴바 */}
      <div className='menuBar2'>
        <Navbar className='noMaPa'>
            <Container>
              <Nav>
                {menu2.map((data,i)=>{return(
                  <Nav.Link className='noMaPa'>{data}</Nav.Link>
                )})}
              </Nav>
            </Container>
          </Navbar>
      </div>

      {/* 스크롤바 */}
      <div className='scroll'>scroll</div>



      {/* 컨텐츠 */}
      <div className='contents' style={{display:'inline-block'}}>
        <Routes>
          <Route path="/" element={'contents'} />
        </Routes>
      </div>

      {/* 푸터 */}
      <div className='footer'>footer(제작자..블라블라)</div>
    </div>
  );






  function selectMenu(category){
    var tempMenuList = []
    for(var key in DB){
      if(DB.key.category == category){
        console.log('여기')
        if(DB.key.series == 'Common'){
          tempMenuList.push(MENU_BTN.se0Btn)
        }else if(DB.key.series == 'IronMan1'){
          tempMenuList.push(DMENU_BTNB.se1Btn)
        }else if(DB.key.series == 'IronMan2'){
          tempMenuList.push(MENU_BTN.se2Btn)
        }else if(DB.key.series == 'IronMan3'){
          tempMenuList.push(MENU_BTN.se3Btn)
        }else if(DB.key.series == 'CrossOver'){
          tempMenuList.push(MENU_BTN.se4Btn)
        }
      }

    }
    console.log(tempMenuList)
    return tempMenuList
  }
}

export default App;
