import { useState } from 'react';
import { Container, Nav, Navbar } from 'react-bootstrap';
import { Route, Routes } from 'react-router-dom';
import './App.css';
import database,{menuButton} from './data/db';

// 이펙트 : 로딩스피너, 스크롤바, 페이지 전환, 메뉴바 상단고정
function App() {
  const DB = database()  // DB 사용법 : DB.key
  let [menu2, setMenu2] = useState([])

  let [showMenu2, setShowMenu2] = useState(false)

  return (
    <div className='container'>
      {/* 헤더 */}
      <div className='header'>header</div>

      {/* 메뉴바 */}
      <div className='menuBar'>
        <Navbar className='noMaPa'>
          <Container>
            <Nav>
              <Nav.Link className='noMaPa' onClick={()=>{
                setMenu2(selectMenu('introduceIronMan'))
              }}>{DB.menuButton.ca0Btn.btn}</Nav.Link>
