              <Nav.Link className='noMaPa' onClick={()=>{

              }}>{DB.menuButton.ca3Btn.btn}</Nav.Link>
              <Nav.Link className='noMaPa' onClick={()=>{

              }}>{DB.menuButton.ca4Btn.btn}</Nav.Link>
              <Nav.Link className='noMaPa' onClick={()=>{

              }}>{DB.menuButton.ca5Btn.btn}</Nav.Link>
              <Nav.Link className='noMaPa' onClick={()=>{

              }}>{DB.menuButton.ca6Btn.btn}</Nav.Link>
            </Nav>
          </Container>
        </Navbar>
      </div>
      {/* 하위메뉴바 */}
      <div className='menuBar2'>
        <Navbar className='noMaPa'>
            <Container>
              <Nav>
                {menu2.map((data,i)=>{return(
                  <Nav.Link className='noMaPa'>{data}</Nav.Link>
                )})}
              </Nav>
            </Container>
          </Navbar>
      </div>

      {/* 스크롤바 */}
      <div className='scroll'>scroll</div>



      {/* 컨텐츠 */}
      <div className='contents' style={{display:'inline-block'}}>
        <Routes>
          <Route path="/" element={'contents'} />
        </Routes>
      </div>

      {/* 푸터 */}
      <div className='footer'>footer(제작자..블라블라)</div>
    </div>
  );



  function selectMenu(category){
    var tempContent = DB.content
    console.log(tempContent)
    var tempMenuList = []
    for(var obj of tempContent){
      if(obj.category == category){
        if(obj.series == 'Common'){
          tempMenuList.push(DB.menuButton.se0Btn.btn)
        }else if(obj.series == 'IronMan1'){
          tempMenuList.push(DB.menuButton.se1Btn.btn)
        }else if(obj.series == 'IronMan2'){
          tempMenuList.push(DB.menuButton.se2Btn.btn)
        }else if(obj.series == 'IronMan3'){
          tempMenuList.push(DB.menuButton.se3Btn.btn)
        }else if(obj.series == 'CrossOver'){
          tempMenuList.push(DB.menuButton.se4Btn.btn)
        }
      }
    }
    return tempMenuList
  }



}// App 종료
export default App;
